import java.util.ArrayList;public class MatchAllFilter implements Filter{    private ArrayList<Filter> filters;    MatchAllFilter(ArrayList<Filter> f){        this.filters=f;    }    void addFilter(Filter f){        filters.add(f);    }    @Override    public boolean satisfies(QuakeEntry qe) {        for(Filter f: filters){            if(!f.satisfies(qe)){                return false;            }        }        return true;    }    public String getName(){        StringBuilder sb = new StringBuilder("Filters used are: ");        for(Filter f: filters){            sb.append(f.getName()).append(" ");        }        return sb.toString();    }}