public class PhraseFilter implements Filter {    private String req;    private String phrase;    PhraseFilter(String r, String p){        this.req=r;        this.phrase=p;    }    @Override    public boolean satisfies(QuakeEntry qe) {        switch (req){            case "start":                if(qe.getInfo().length() > phrase.length() && qe.getInfo().substring(0, phrase.length()).equals(phrase)){                    return true;                }                break;            case "end":                if(qe.getInfo().length() > phrase.length() && qe.getInfo().substring(qe.getInfo().length()-phrase.length()).equals(phrase)){                    return true;                }                break;            default:                if(qe.getInfo().contains(phrase)){                    return true;                }        }        return false;    }    public String getName(){return "Phrase";}}